final class Hydrogen_Typography_Manager {

	private $less_vars = array();

	private static $instance;

	private function __construct() {}

	public static function get() {

		if( ! is_a( self::$instance, get_class() ) ) {
			self::$instance = new Hydrogen_Typography_Manager();
		}

		return self::$instance;
	}

	/**
	 * Get all font family targets with the 'Novecento' font family
	 */
	public function novecento_targets() {

		$default = new Youxi_CSS_Font(
			sanitize_key( 'Novecento' ), 
			'"Novecento Sans Wide", "Helvetica Neue", Helvetica, Arial, sans-serif', 
			array(
				'src' => get_template_directory_uri() . "/assets/fonts/novecento/stylesheet.css", 
				'deps' => array(), 
				'ver' => '1.0'
			)
		);

		$targets = array(
			'headings_alt_font', 
			'splash_headline_font', 
			'section_title_font', 
			'section_subtitle_font', 
			'section_separator_title_font', 
			'page_title_font', 
			'team_popup_name_font', 
			'counter_number_font', 
			'pricing_table_price_font', 
			'slider_media_caption_font', 
			'recent_post_title_font', 
			'post_title_font', 
			'post_comments_reply_title_font', 
			'post_comments_count_font', 
			'post_comments_name_font', 
			'footer_widget_title_font'
		);

		return array_fill_keys( $targets, $default );
	}

	/**
	 * Get all font family targets with the 'Raleway' font family
	 */
	public function raleway_targets() {

		$default = new Youxi_Google_Font(
			sanitize_key( 'Raleway' ), 
			'"Raleway", "Helvetica Neue", Helvetica, Arial, sans-serif', 
			'Raleway', 
			array( '300', 'regular', '600', '700' )
		);

		$targets = array(
			'body_font', 
			'btn_font', 
			'headings_default_font', 
			'main_nav_anchor_font', 
			'splash_intro_font', 
			'splash_description_font', 
			'splash_feedback_font', 
			'section_title_small_font', 
			'section_subtitle_small_font', 
			'section_separator_title_small_font', 
			'page_title_small_font', 
			'team_popup_role_font', 
			'testimonial_font', 
			'tweet_text_font', 
			'call_to_action_headline_font', 
			'pricing_table_name_font', 
			'pricing_table_price_description_font', 
			'footer_text_font'
		);

		return array_fill_keys( $targets, $default );
	}

	/**
	 * Get all font family targets with the 'Droid Serif' font family
	 */
	public function droidserif_targets() {

		$default = new Youxi_Google_Font(
			sanitize_key( 'Droid Serif' ), 
			'"Droid Serif", Georgia, Times, "Times New Roman", serif', 
			'Droid Serif', 
			array( 'regular', 'italic' )
		);

		$targets = array( 'blockquote_font' );

		return array_fill_keys( $targets, $default );
	}

	/**
	 * Get all font family targets
	 */
	public function targets() {
		return array_merge( $this->novecento_targets(), $this->raleway_targets(), $this->droidserif_targets() );
	}

	/**
	 * Get all font style arguments to be passed to wp_register_style
	 */
	public function get_styles() {

		$wp_theme = wp_get_theme();
		$theme_version = $wp_theme->exists() ? $wp_theme->get( 'Version' ) : false;

		$manager = Youxi_Fonts_Manager::get();

		return array_merge( array(
			'google-fonts' => array(
				'src'   => $manager->get_google_fonts_request_url(), 
				'deps'  => array(), 
				'ver'   => $theme_version, 
				'media' => 'all'
			)
		), $manager->get_css_fonts_style_args() );
	}

	/**
	 * Prepare fonts and LESS variables to be compiled
	 */
	public function prepare() {

		/* Get the fonts manager */
		$manager = Youxi_Fonts_Manager::get();

		/* Get the options array for a quicker access */
		$ot_options = get_option( ot_options_id() );

		/* Prepare empty vars */
		$this->less_vars = array();

		/* Register required fonts */
		foreach( $this->targets() as $target => $default ) {

			if( isset( $ot_options[ $target ] ) ) {

				$opt = wp_parse_args( (array) $ot_options[ $target ], array(
					'font-family' => '', 
					'font-variant' => '', 
					'font-subsets' => array()
				));

				/* We have selected a Google Font */
				if( '' !== $opt['font-family'] ) {

					/* Prepare Google Font arguments */
					$handle   = sanitize_key( $opt['font-family'] );
					$family   = Youxi_Google_Font::id2family( $opt['font-family'] );
					$variants = array( $opt['font-variant'] );
					$subsets  = $opt['font-subsets'];

					/* If the font is not yet registered, register it */
					if( ! $manager->has_font( $handle ) ) {

						$font = new Youxi_Google_Font( $handle, sprintf( '"%s"', $family ), $family, $variants, $subsets );
						$manager->add_font( $font );

					} else {

						/* Update if it already exists, and it's a google font */
						$font = $manager->get_font( $handle );
						if( is_a( $font, 'Youxi_Google_Font' ) ) {
							$font->update( compact( 'variants', 'subsets' ) );
						}
					}

					if( is_a( $font, 'Youxi_Google_Font' ) ) {

						$var_name = str_replace( '_', '-', $target );
						$this->less_vars[ "{$var_name}-family" ] = $font->font_family();

						if( '' !== $opt['font-variant'] ) {

							$this->less_vars[ "{$var_name}-weight" ] = Youxi_Google_Font::normalize_weights( $opt['font-variant'] );

							if( preg_match( '/italic/', $opt['font-variant'] ) ) {
								$this->less_vars[ "{$var_name}-style" ] = 'italic';
							}
						}
					}

				} else {

					/* Use the default font */
					if( is_a( $default, 'Youxi_Font' ) ) {

						/* Try getting a font with the same ID as the default font */
						$font = $manager->get_font( $default->id() );

						if( ! is_a( $font, 'Youxi_Font' ) ) {

							/* If it doesn't exists, we'll just register the default font */
							$manager->add_font( $default );

						} else {

							/* A font with the same ID as the default font already exists, let's update it if it's a Google Font */
							if( is_a( $font, 'Youxi_Google_Font' ) ) {
								$font->update( $default );
							}
						}

					}

				}
			}
		}
	}

	/**
	 * Filter OptionTree recognized google font variants
	 */
	public function ot_recognized_google_font_variants( $variants ) {

		foreach( $variants as $key => $variant ) {
			if( preg_match( '/italic/', $variant ) ) {
				unset( $variants[ $key ] );
			}
		}

		return $variants;
	}

	/**
	 * Filter OptionTree recognized google font fields
	 */
	public function ot_recognized_google_font_fields( $array ) {
		return array( 'font-variant' );
	}

	/**
	 * Get the LESS variables for compiling the stylesheet
	 */
	public function get_less_vars() {
		
		/* Prepare variables */
		$prepared_vars = array();
		$prepared_vars[ md5( get_class() ) ] = $this->less_vars;

		return $prepared_vars;
	}

